INSERT INTO `ec_products_multilingual` VALUES ('25', '20', '1', 'PF8', '<p>Les imprimantes bureau PF8 disposent d&#39;un encombrement r&eacute;duit <span style=\"color:#000080\"><strong>id&eacute;al dans un espace limit&eacute;</strong></span>.<br />\r\n<br />\r\nElles sont con&ccedil;ues d&#39;une <span style=\"color:#000080\"><strong>double coque</strong></span> pour un <span style=\"color:#000080\"><strong>chargement des consommables simple et intuitif</strong></span>.<br />\r\n<br />\r\nCe sont des solutions adapt&eacute;es aux secteurs de la sant&eacute;, le transport, le courrier, le commerce de d&eacute;tail, les loisirs et la bureautique.<br />\r\n<br />\r\nDot&eacute;es d&#39;une r&eacute;solution de 203 ou 300dpi, elles editent des &eacute;tiquettes jusqu&#39;&agrave; <span style=\"color:#000080\"><strong>106mm de large</strong></span>.<br />\r\n<br />\r\nCe mat&eacute;riel est toujours disponible chez Eurocomputer.</p>\r\n', '0', '0', 'Intermec_PF8D.pdf', '<p>PF8DA03000000<br />\r\nPF8TA03000000<br />\r\nPF8TA03001000</p>\r\n\r\n<p>imprimante intermec PF8</p>\r\n', '', '', '', '', '', '', '', '12;15;16', '109;110;111;112;114;116', '203 ou 300dpi', '101.6mm/s', '106mm', 'direct thermique (DT) 2kg - transfert thermique (TT) 2,5kg', 'DT 168x116x218mm - TT 226x172x285mm', 'bureautique', 'Intermec PF8', '<p>PF8DA03000000<br />\r\nPF8TA03000000<br />\r\nPF8TA03001000</p>\r\n\r\n<p><a href=\"https://www.eurocomputer.fr/imprimantes-etiquettes/intermec/petits-modeles/pc41-393.html\">intermec PC41</a></p>\r\n\r\n<p><a href=\"https://www.eurocomputer.fr/imprimantes-etiquettes/intermec/petits-modeles/pc43-376.html\">intermec PC43</a></p>\r\n');
