INSERT INTO `ec_products_multilingual` VALUES ('47', '31', '1', 'ZT410', '<p style=\"text-align:justify\">Les &nbsp;Zebra ZT410 ont &eacute;t&eacute; con&ccedil;ues pour &ecirc;tre performantes pendant de nombreuses ann&eacute;es, m&ecirc;me dans des <span style=\"color:#000080\"><strong>milieux difficiles</strong></span>, elles sont robustes gr&acirc;ce &agrave; leur <span style=\"color:#000080\"><strong>ch&acirc;ssis tout m&eacute;tal</strong></span> .&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#000080\"><strong>Faciles &agrave; utiliser</strong></span>, elles offrent une interface utilisateur graphique LCD intuitive &agrave; base d&rsquo;ic&ocirc;nes, et les consommables simples &agrave; charger.&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">Elles proposent &eacute;galement des connexions USB, Ehternet et Bluetooth en standard, <span style=\"color:#000080\"><strong>Wifi</strong></span> et <span style=\"color:#000080\"><strong>RFID</strong></span> en option.</p>\r\n\r\n<p style=\"text-align:justify\">D&eacute;clin&eacute;es&nbsp;en 203, 300dpi ou<span style=\"color:#000080\"><strong> 600dpi</strong></span>, elles permettent des impressions jusqu&#39;&agrave; <span style=\"color:#000080\"><strong>104mm de large</strong></span>.</p>\r\n\r\n<p style=\"text-align:justify\">Ce mat&eacute;riel disponible chez Eurocomputer remplace au catalogue les <a href=\"http://www.eurocomputer.fr/imprimantes-etiquettes/zebra/gamme-semi-industrielle/zm400-25.html\"><span style=\"color:#000080\"><u><strong>ZM400</strong></u></span></a>.</p>\r\n', '0', '0', 'Zebra_ZT400_FR.pdf', '<p>ZT41042-T0E0000Z<br />\r\nZT41042-T0E00C0Z<br />\r\nZT41042-T0EC000Z<br />\r\nZT41042-T1E0000Z<br />\r\nZT41042-T2E0000Z<br />\r\nZT41042-T3E0000Z<br />\r\nZT41042-T4E0000Z<br />\r\nZT41043-T0E0000Z<br />\r\nZT41043-T0E00C0Z<br />\r\nZT41043-T1E0000Z<br />\r\nZT41043-T2E0000Z<br />\r\nZT41043-T3E0000Z<br />\r\nZT41043-T4E0000Z<br />\r\nZT41046-T0E0000Z<br />\r\nZT41046-T4E0000Z<