INSERT INTO `ec_products_multilingual` VALUES ('49', '32', '1', 'ZT420', '<p style=\"text-align:justify\">Les &nbsp;Zebra ZT420 ont &eacute;t&eacute; con&ccedil;ues pour &ecirc;tre performantes pendant de nombreuses ann&eacute;es, m&ecirc;me dans des <span style=\"color:#000080\"><strong>milieux difficiles</strong></span>, elles sont <span style=\"color:#000080\"><strong>robustes </strong></span>gr&acirc;ce &agrave; leur ch&acirc;ssis tout m&eacute;tal .&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#000080\"><strong>Faciles &agrave; utiliser</strong></span>, elles offrent une interface utilisateur graphique LCD intuitive &agrave; base d&rsquo;ic&ocirc;nes, et les consommables simples &agrave; charger.&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">Elles proposent &eacute;galement des connexions USB, Ehternet et Bluetooth en standard, <span style=\"color:#000080\"><strong>Wifi </strong></span>et <span style=\"color:#000080\"><strong>RFID </strong></span>en option.</p>\r\n\r\n<p style=\"text-align:justify\">D&eacute;clin&eacute;es&nbsp;en 203 ou 300dpi, elles permettent des impressions jusqu&#39;&agrave; <span style=\"color:#000080\"><strong>168mm de large</strong></span>.</p>\r\n\r\n<p style=\"text-align:justify\">Ce mat&eacute;riel disponible chez Eurocomputer remplace au catalogue les <a href=\"http://www.eurocomputer.fr/imprimantes-etiquettes/zebra/gamme-semi-industrielle/zm600-26.html\"><span style=\"color:#000080\"><u><strong>ZM600</strong></u></span></a>.</p>\r\n', '0', '0', 'Zebra_ZT400.pdf', '<p>ZT42062-T0E0000Z<br />\r\nZT42062-T0E00C0Z<br />\r\nZT42062-T0EC000Z<br />\r\nZT42062-T2E0000Z<br />\r\nZT42062-T4E0000Z<br />\r\nZT42063-T0E0000Z<br />\r\nZT42063-T0E00C0Z<br />\r\nZT42063-T2E0000Z<br />\r\nZT42063-T4E0000Z</p>\r\n\r\n<p>imprimante zebra ZT420</p>\r\n', '', '', '', '', '', '', '', '26;30;78', '109;110;111;112;113;115;114;116', '203 ou 300dpi', '305mm/s', '168mm', '18,14kg', '336x324x495mm', 'semi-industrielle', 'Zebra ZT420'