INSERT INTO `ec_products_multilingual` VALUES ('79', '47', '1', 'BEX4-T2', '<p>La BEX4-T2 est une imprimante semi-industrielle <strong><span style=\"color:#000080\">facile &agrave; utiliser et maintenir</span></strong> gr&acirc;ce au contr&ocirc;le du ruban par double moteur et les t&ecirc;tes d&#39;impression encliquetables.&nbsp;</p>\r\n\r\n<p>Elle propose de nombreuses connexions : USB, r&eacute;seau, s&eacute;rie, parall&egrave;le et <span style=\"color:#000080\"><strong>Wifi</strong></span>.</p>\r\n\r\n<p>Disponible en 203, 300dpi ou 600dpi, elle permet des impressions jusqu&#39;&agrave; <span style=\"color:#000080\"><strong>104mm de large</strong></span>.</p>\r\n\r\n<p>Ce mat&eacute;riel,&nbsp;disponible chez Eurocomputer, remplace au catalogue les <a href=\"http://www.eurocomputer.fr/imprimantes-etiquettes/toshiba-tec/gamme-semi-industrielle/bsx4-46.html\"><span style=\"color:#000080\"><u><strong>BSX4</strong></u></span></a>.</p>\r\n', '0', '0', 'TEC-B-EX4T2.pdf', '<p>BEX4D2GS12QMR<br />\r\nBEX4T2GS12QMR<br />\r\nBEX4T2TS12QMR<br />\r\nBEX4T2HS12QMR</p>\r\n\r\n<p>imprimante toshiba tec &nbsp;BEX4-T2</p>\r\n', '', '', '', '', '', '', '', '46;25;43', '109;110;111;112;113;115;114;116', '203,300 ou 600dpi', '304mm/s', '104mm', '20kg', '278x310x460mm', 'semi-industrielle', 'Toshiba TEC BEX4-T2', '<p>BEX4D2GS12QMR<br />\r\nBEX4T2GS12QMR<br />\r\nBEX4T2TS12QMR<br />\r\nBEX4T2HS12QMR</p>\r\n\r\n<p><a href=\"https://www.eurocomputer.fr/imprimantes-etiquettes/toshiba-tec/gamme-semi-industrielle/bsx4-46.html\">toshiba tec BSX4</a></p>\r\n\r\n<p><a href=\"https://www.eurocomputer.fr/imprimantes-etiquettes/toshiba-tec/gamme-semi-industrielle/b852-49.html\">toshiba tec B852</a></p>\r\n');
