INSERT INTO `ec_products_multilingual` VALUES ('83', '49', '1', 'B852', '<p>L&#39;imprimante semi-industrielle B852 permet d&#39;&eacute;diter des &eacute;tiquettes jusqu &#39;&agrave; <span style=\"color:#000080\"><strong>216,8mm de large</strong></span>. Elle est<span style=\"color:#000080\"><strong> id&eacute;ale en remplacement d&#39;une laser</strong></span> pour l&#39;&eacute;dition d&#39;&eacute;tiquettes et de documents<span style=\"color:#000080\"><strong> A4</strong></span>.</p>\r\n\r\n<p>Sa t&ecirc;te de 8,5 pouces en <span style=\"color:#000080\"><strong>300dpi</strong></span> restitue une superbe impression de <span style=\"color:#000080\"><strong>haute r&eacute;solution</strong></span>.</p>\r\n\r\n<p>Elle est robuste gr&acirc;ce &agrave; son <span style=\"color:#000080\"><strong>capot en acier</strong></span>.</p>\r\n\r\n<p>Ce mat&eacute;riel est disponible chez Eurocomputer.</p>\r\n', '0', '0', 'TEC_B852.pdf', '<p>B852TS22QPR<br />\r\nB852TS12QP</p>\r\n\r\n<p>Imprimante toshiba tec B852</p>\r\n', '', '', '', '', '', '', '', '64;68;72', '109;110;111;112;113;115;114;116', '300dpi', '101,6mm/s', '216,8mm', '15,6kg', '385x243x182mm', 'semi-industrielle', 'Toshiba TEC B852', '<p>B852TS22QPR<br />\r\nB852TS12QP</p>\r\n\r\n<p><span style=\"font-family:arial,sans-serif; font-size:10pt\"><a href=\"https://www.eurocomputer.fr/imprimantes-etiquettes/toshiba-tec/gamme-semi-industrielle/bsx4-46.html\">toshiba tec BSX4</a></span></p>\r\n\r\n<p><span style=\"font-family:arial,sans-serif; font-size:10pt\"><a href=\"https://www.eurocomputer.fr/imprimantes-etiquettes/toshiba-tec/gamme-semi-industrielle/bex4-t2-47.html\">toshiba tec BEX4-T2</a></span></p>\r\n');
