INSERT INTO `ec_products_multilingual` VALUES ('107', '61', '1', '110Xi3', '<p style=\"text-align:justify\">Les imprimantes industrielles 110xi3&nbsp;sont robustes et durables avec leur&nbsp;<span style=\"color:rgb(0, 0, 128)\"><strong>ch&acirc;ssis en acier</strong></span>.</p>\r\n\r\n<p style=\"text-align:justify\">Ces imprimantes haut de gamme offrent des vitesses d&#39;impression jusqu&#39;&agrave;&nbsp;<span style=\"color:rgb(0, 0, 128)\"><strong>254mm/s</strong></span>&nbsp;id&eacute;ales pour du&nbsp;<span style=\"color:rgb(0, 0, 128)\"><strong>gros volume.</strong></span></p>\r\n\r\n<p style=\"text-align:justify\">D&eacute;clin&eacute;es en 203, 300 ou&nbsp;<span style=\"color:rgb(0, 0, 128)\"><strong>600dpi</strong></span>, elles permettent d&#39;&eacute;diter des &eacute;tiquettes avec de&nbsp;<span style=\"color:rgb(0, 0, 128)\"><strong>tr&egrave;s petits caract&egrave;res</strong></span>&nbsp;et jusqu&#39;&agrave;<span style=\"color:rgb(0, 0, 128)\"><strong>104mm de large.</strong></span></p>\r\n\r\n<p style=\"text-align:justify\">Ce mat&eacute;riel est toujours disponible chez Eurocomputer.</p>\r\n', '0', '0', '', '<p>IMPRIMANTE ZEBRA&nbsp;&nbsp;110xi3</p>\r\n', '', '', '', '', '', '', '', '26;65;78', '109;110;111;112;113;115;114;116', '203,300 ou 600dpi', '254mm/s', '102mm', '22,7kg', '262x394x518mm', 'industrielle', 'Zebra 110Xi3', '<p><a href=\"https://www.eurocomputer.fr/imprimantes-etiquettes/zebra/gamme-industrielle/110xi3--65.html\">zebra 110xi3+</a></p>\r\n\r\n<p><a href=\"https://www.eurocomputer.fr/imprimantes-etiquettes/zebra/gamme-industrielle/110xi4-69.html\">zebra 110xi4</a></p>\r\n\r\n<p><a href=\"https://www.eurocomputer.fr/imprimantes-etiquettes/zebra/gamme-industrielle/140xi3-62.html\">zebra 140xi3</a></p>\r\n\r\n<p><a href=\"https://www.eurocomputer.fr/imprimantes-etiquettes/zebra/gamme-industrielle/140xi3--66.html\">zebra 140xi3+</a></p>\r\n\r\n<p><a href=\"https://www.eurocomputer.fr/imprimantes-etiquettes/zebra/gamme-industrielle/1