INSERT INTO `ec_products_multilingual` VALUES ('175', '95', '1', 'QL220+', '<p>L&#39;imprimante mobile&nbsp;QL 220 Plus&nbsp;est solide et l&eacute;g&egrave;re : <span style=\"color:#000080\"><strong>surmoulage en plastique caoutchout&eacute;</strong></span>, r&eacute;sistance aux chutes de 1,5m et&nbsp;<span style=\"color:#000080\"><strong>poids inf&eacute;rieur &agrave; 1 Kg</strong></span>.<br />\r\n<br />\r\nElle est&nbsp;facile&nbsp;&agrave; utliser avec un <span style=\"color:#000080\"><strong>chargement simple des consommables.</strong></span><br />\r\n<br />\r\nGr&acirc;ce au <span style=\"color:#000080\"><strong>r&eacute;seau sans fil</strong></span> (802.11b) ou au&nbsp;<span style=\"color:#000080\"><strong>Bluetooth</strong></span>, elle est parfaitement adapt&eacute;e aux applications terrain dans les secteurs postaux, commerciaux et&nbsp;la sant&eacute;.&nbsp;<br />\r\n<br />\r\nDisposant d&#39;une r&eacute;solution de 203dpi, elle permet d&#39;&eacute;diter des &eacute;tiquettes jusqu&#39; &agrave; <span style=\"color:#000080\"><strong>48mm de large</strong></span>. &nbsp;<br />\r\n<br />\r\nCe mat&eacute;riel est toujours disponible chez Eurocomputer.</p>\r\n', '0', '0', 'Zebra-QLplus-series-fr.pdf', '<p>Q2D-LU1CE010-00<br />\r\nQ2D-LUBCE010-00<br />\r\nQ2D-LUKCE010-00<br />\r\nQ2D-LU1DE010-00<br />\r\nQ2D-LUBDE010-00<br />\r\nQ2D-LUKDE010-00</p>\r\n\r\n<p>imprimante zebra QL220+</p>\r\n', '', '', '', '', '', '', '', '93', '109;110;112;113', '203dpi', '76,2mm/s', '48mm', '0,57kg', '90,7x181,6x75,2mm', 'mobile', 'Zebra QL220+', '<p>Q2D-LU1CE010-00<br />\r\nQ2D-LUBCE010-00<br />\r\nQ2D-LUKCE010-00<br />\r\nQ2D-LU1DE010-00<br />\r\nQ2D-LUBDE010-00<br />\r\nQ2D-LUKDE010-00</p>\r\n\r\n<p><a href=\"https://www.eurocomputer.fr/imprimantes-etiquettes/zebra/imprimantes-portables/rw420-375.html\">zebra RW420</a></p>\r\n\r\n<p><a href=\"https://www.eurocomputer.fr/imprimantes-etiquettes/zebra/imprimantes-portables/ql220-93.html\">zebra QL220</a></p>\r\n\r\n<p><a