INSERT INTO `ec_products_multilingual` VALUES ('283', '149', '1', 'Memor X3', '<p style=\"text-align:justify\">Compact et l&eacute;ger, le Memor procure une excellente <span style=\"color:#000080\"><strong>ergonomie </strong></span>et une grande facilit&eacute; d&rsquo;utilisation. De conception tr&egrave;s <span style=\"color:#000080\"><strong>robuste</strong></span>, il r&eacute;siste&nbsp;aux chutes accidentelles, aux projections d&rsquo;eau et &agrave; la poussi&egrave;re.</p>\r\n\r\n<p style=\"text-align:justify\">Id&eacute;al pour les <span style=\"color:#000080\"><strong>inventaires</strong></span>, il est dot&eacute; d&#39;une grande capacit&eacute; m&eacute;moire et d&#39;un emplacement pour carte SD pour g&eacute;rer une grande base de donn&eacute;es.</p>\r\n\r\n<p style=\"text-align:justify\">Son scanner laser est &eacute;quip&eacute; du<span style=\"color:#000080\"><strong> Spot Vert,</strong></span> unique &agrave; Datalogic, qui confirme la bonne lecture des code-barres.</p>\r\n\r\n<p style=\"text-align:justify\">Ce mat&eacute;riel est <span style=\"color:#000080\"><strong>toujours disponible</strong></span> chez Eurocomputer.</p>\r\n', '0', '0', 'Datalogic_Memor.pdf', '<p>944201039<br />\r\n944201041<br />\r\n944201038<br />\r\n944201040<br />\r\n944201016<br />\r\n944250002<br />\r\n944250001<br />\r\n944250005<br />\r\n944250004<br />\r\n944250003</p>\r\n\r\n<p><span style=\"font-family:arial,sans-serif; font-size:10pt\">scanner&nbsp;</span>Memor X3</p>\r\n\r\n<p><span style=\"font-family:arial,sans-serif; font-size:10pt\">lecteur&nbsp;</span>Memor X3</p>\r\n\r\n<p><span style=\"font-family:arial,sans-serif; font-size:10pt\">terminal&nbsp;</span>Memor X3</p>\r\n\r\n<p><span style=\"font-family:arial,sans-serif; font-size:10pt\">douchette&nbsp;</span>Memor X3</p>\r\n\r\n<p><span style=\"font-family:arial,sans-serif; font-size:10pt\">scanner code-barres&nbsp;</span>Memor X3</p>\r\n\r\n<p><span style=\"font-family:arial,sans-serif; font-size:10pt\">lec