INSERT INTO `ec_products_multilingual` VALUES ('301', '158', '1', 'DS4208 2D', '<p>La douchette filaire Symbol Motorola DS4208 lit les code-barres 1D et <strong><span style=\"color:#000080\">2D</span></strong> sur de <span style=\"color:#000080\"><strong>nombreux supports</strong></span> : aussi bien sur &eacute;tiquettes papiers, &eacute;crans d&#39;ordinateurs ou t&eacute;l&eacute;phones portables.</p>\r\n\r\n<p>Sa forme <span style=\"color:#000080\"><strong>ergonomique </strong></span>facilite son usage au quotidien. Elle est &eacute;galement robuste, et r&eacute;siste aux <span style=\"color:#000080\"><strong>chutes de 1,83m sur b&eacute;ton</strong></span>.</p>\r\n\r\n<p>Elle dispose d&#39;une <span style=\"color:#000080\"><strong>port&eacute;e de lecture allant jusqu &#39;&agrave; 38,6cm</strong></span> (SR Focus Range).</p>\r\n\r\n<p>D&eacute;clin&eacute;e en USB ou s&eacute;rie, ce mat&eacute;riel est toujours disponible chez Eurocomputer.</p>\r\n', '0', '0', 'Motorola_DS4208-2D.pdf', '<p>DS4208-SR00007WR<br />\r\nDS4208-SR00007Y<br />\r\nDS4208-DL00001WR<br />\r\nDS4208-DL00007WR<br />\r\nDS4208-HC00009WR<br />\r\nDS4208-HD00007WR</p>\r\n\r\n<p><span style=\"font-family:arial,sans-serif; font-size:10pt\">scanner&nbsp;</span>DS4208 2D</p>\r\n\r\n<p><span style=\"font-family:arial,sans-serif; font-size:10pt\">lecteur&nbsp;</span>DS4208 2D</p>\r\n\r\n<p><span style=\"font-family:arial,sans-serif; font-size:10pt\">terminal&nbsp;</span>DS4208 2D</p>\r\n\r\n<p><span style=\"font-family:arial,sans-serif; font-size:10pt\">douchette&nbsp;</span>DS4208 2D</p>\r\n\r\n<p><span style=\"font-family:arial,sans-serif; font-size:10pt\">scanner code-barres&nbsp;</span>DS4208 2D</p>\r\n\r\n<p><span style=\"font-family:arial,sans-serif; font-size:10pt\">lecteur code-barres&nbsp;</span>DS4208 2D</p>\r\n\r\n<p><span style=\"font-family:arial,sans-serif; font-size:10pt\">&nbsp;terminal code-barres&nbsp;</span>DS4208 2D</p>\r\n\r\n<p><span style=\"font-family:arial,sans-serif; fon