INSERT INTO `ec_products_multilingual` VALUES ('311', '163', '1', 'SG10', '<p>La douchette filaire Intermec SG10T est un lecteur <span style=\"color:#000080\"><strong>simple, abordable et fiable</strong></span> con&ccedil;u pour les petites et moyennes entreprises.</p>\r\n\r\n<p><span style=\"color:#000080\"><strong>Simple &agrave; utiliser,</strong></span> vous pouvez l&#39;utiliser d&egrave;s la sortie de son emballage, il suffit de le brancher et d&#39;entamer la lecture.</p>\r\n\r\n<p>Sa <span style=\"color:#000080\"><strong>port&eacute;e de lecture s&#39;&eacute;tend jusqu&#39;&agrave; 12cm</strong></span>.</p>\r\n\r\n<p>D&eacute;clin&eacute; avec une interface <span style=\"color:#000080\"><strong>USB </strong></span>ou &eacute;cran-clavier (Wedge), ce mat&eacute;riel est disponible chez Eurocomputer.</p>\r\n', '0', '0', 'Intermec_SG10.pdf', '<p>SG10T1D-KBW001<br />\r\nSG10T1D-USB001</p>\r\n', '', '', '', '', '', '', '', '165;156;159', '344;345;346;347;348', 'filaire', 'USB, clavier Wedge', '1D', '12cm', '67x164x54mm', '105g', 'Intermec / Honeywell SG10', '');
