INSERT INTO `ec_products_multilingual` VALUES ('315', '165', '1', 'OPL6845', '<p>La douchette filaire Opticon OPL6845 est une douchette compacte et <span style=\"color:#000080\"><strong>&eacute;conomique</strong></span>.</p>\r\n\r\n<p>Elle est propos&eacute;e avec de multiples connectiques : <span style=\"color:#000080\"><strong>USB</strong></span>, Wedge clavier, s&eacute;rie RS232.<span style=\"line-height:1.6em\">&nbsp;</span></p>\r\n\r\n<p>Sa port&eacute;e de lecture s&#39;&eacute;tend <span style=\"color:#000080\"><strong>jusqu &#39;&agrave; 32,2cm</strong></span>.</p>\r\n\r\n<p>Ce mat&eacute;riel est toujours disponible chez Eurocomputer.</p>\r\n', '0', '0', 'Opticon_OPL6845.pdf', '<p>12091<br />\r\n12092<br />\r\n12093<br />\r\n12088<br />\r\n12098<br />\r\n12090</p>\r\n', '', '', '', '', '', '', '', '159;156', '344;345;346;347;348', 'filaire', 'USB, clavier Wedge, série RS232', '1D', '32,2cm', '47x159x32mm', '90g', 'Opticon OPL6845', '');
