INSERT INTO `ec_products_multilingual` VALUES ('317', '166', '1', 'LI4278', '<p>La douchette sans fil Motorola LI4278 lit les code-barres 1D sur de <strong><span style=\"color:#000080\">nombreux supports</span></strong> : &eacute;tiquettes papier, &eacute;crans de t&eacute;l&eacute;phones portables, d&#39;une tablette ou d&#39;un ordinateur.<br />\r\n<br />\r\nSa technologie <span style=\"color:#000080\"><strong>Bluetooth </strong></span>lib&egrave;re les utlisateurs des contraintes des &eacute;quipements filaires, avec une autonomie pour une journ&eacute;e de travail enti&egrave;re.<br />\r\n<br />\r\n<span style=\"color:#000080\"><strong>Robuste</strong></span>, elle est con&ccedil;ue pour une<strong><span style=\"color:#000080\"> utilisation intensive au quotidien</span></strong>, dans les environnements poussi&eacute;reux et humides. La ligne de lecture reste visible &agrave; l&#39;ext&eacute;rieur et aussi dans des conditions de faible luminosit&eacute;.<br />\r\n<br />\r\nElle dispose d&#39;une <span style=\"color:#000080\"><strong>port&eacute;e de lecture allant jusqu &#39;&agrave; 106,7cm</strong></span>.<br />\r\n<br />\r\nCe mat&eacute;riel est toujours disponible chez Eurocomputer.</p>\r\n', '0', '0', 'Motorola-LI4278.pdf', '<p>LI4278-SR20001WR<br />\r\nLI4278-SR20007WR<br />\r\nLI4278-PRBU2100AAR<br />\r\nLI4278-PRBU2100AWR<br />\r\nLI4278-PRWU2100AAR<br />\r\nLI4278-PRWU2100AWR<br />\r\nLI4278-SR1R0110SRN<br />\r\nLI4278-SR7R0110SRN<br />\r\nLI4278-TRBU0100ZAR<br />\r\nLI4278-TRBU0100ZER<br />\r\nLI4278-TRBU0100ZWR<br />\r\nLI4278-TRWU0100ZAR<br />\r\nLI4278-TRWU0100ZER<br />\r\nLI4278-TRWU0100ZWR</p>\r\n\r\n<p><span style=\"font-family:arial,sans-serif; font-size:10pt\">scanner&nbsp;</span>LI4278</p>\r\n\r\n<p><span style=\"font-family:arial,sans-serif; font-size:10pt\">lecteur&nbsp;</span>LI4278</p>\r\n\r\n<p><span style=\"font-family:arial,sans-serif; font-size:10pt\">terminal&nbsp;</span>LI4278</p>\r\n\r\n<p><span style=\"font-family:arial,sans-seri