INSERT INTO `ec_products_multilingual` VALUES ('465', '240', '1', 'E360', '<p><span style=\"font-size:14px\"><span style=\"font-family:arial,helvetica,sans-serif\">Les imprimantes laser Lexmark E360 sont <span style=\"color:#000080\"><strong>compactes </strong></span>et <strong><span style=\"color:#000080\">ultra-performantes.</span></strong></span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:arial,helvetica,sans-serif\">Elles sont adapt&eacute;es aux <span style=\"color:#000080\"><strong>utilisateurs individuels</strong></span> et aux <span style=\"color:#000080\"><strong>petits groupes de travail</strong></span>.</span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:arial,helvetica,sans-serif\">Performantes, elles disposent de vitesses d&#39;impression tr&egrave;s &eacute;lev&eacute;es : <span style=\"color:#000080\"><strong>38 ppm</strong></span>.</span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:arial,helvetica,sans-serif\">Intuitives, elles disposent d&#39;un <span style=\"color:#000080\"><strong>&eacute;cran LCD</strong></span> avec affichage sur 2 lignes.</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n', '0', '0', 'Lexmark_E360.pdf', '<p>34S0412<br />\r\n34S0512</p>\r\n', '', '', '', '', '', '', '', '236', '351;353;354;356;366', '38ppm', 'monochrome', 'A4', '850 feuilles', '40,6x26x37cm', 'petit groupe de travail', 'Lexmark E360', '');
