INSERT INTO `ec_products_multilingual` VALUES ('467', '241', '1', 'E460', '<p><span style=\"font-size:14px\"><span style=\"font-family:arial,helvetica,sans-serif\">Les imprimantes laser Lexmark E460 sont <span style=\"color:#000080\"><strong>compactes </strong></span>et <strong><span style=\"color:#000080\">endurantes</span></strong>.</span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:arial,helvetica,sans-serif\">Avec son <span style=\"color:#000080\"><strong>&eacute;cran intuitif</strong></span>, cette imprimante r&eacute;seau est <strong><span style=\"color:#000080\">simple </span></strong>&agrave; utiliser.</span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:arial,helvetica,sans-serif\">Elle dispose de base du <span style=\"color:#000080\"><strong>recto verso </strong></span>et peut atteindre des vitesses d&#39;impression<span style=\"color:#000080\"><strong> jusqu &#39;&agrave; 38ppm.</strong></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n', '0', '0', 'Lexmark_E460.PDF', '<p>34S0712</p>\r\n', '', '', '', '', '', '', '', '236', '351;353;354;356;366', '38ppm', 'monochrome', 'A4', '850 feuilles', '40,6x26x37cm', 'petit groupe de travail', 'Lexmark E460', '');
