INSERT INTO `ec_products_multilingual` VALUES ('469', '242', '1', 'MS410', '<p><span style=\"font-size:14px\"><span style=\"font-family:arial,helvetica,sans-serif\">L&#39;imprimante laser monochrome Lexmark MS410 dipose d&#39;un <strong><span style=\"color:#000080\">d&eacute;bit tr&egrave;s rapide </span></strong>puisque la premi&egrave;re page s&#39;imprime en <span style=\"color:#000080\"><strong>6,5 secondes</strong></span> seulement.</span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:arial,helvetica,sans-serif\">Sa vitesse d&#39;impression peut &eacute;galement atteindre jusqu&#39;&agrave; <span style=\"color:#000080\"><strong>38 pages par minute</strong></span>.</span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:arial,helvetica,sans-serif\"><span style=\"color:#000080\"><strong>Recto verso</strong></span> de base, vous pouvez &eacute;galement la connecter &agrave; votre <span style=\"color:#000080\"><strong>r&eacute;seau </strong></span>(en option).</span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:arial,helvetica,sans-serif\">Elle est&nbsp;<strong><span style=\"color:#000080\">facile &agrave; utiliser</span></strong> gr&acirc;ce &agrave; son panneau de commande &agrave; 2 lignes facile &agrave; lire.</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n', '0', '0', 'Lexmark_MS410.pdf', '<p>35S0170<br />\r\n35S0230</p>\r\n', '', '', '', '', '', '', '', '236', '351;353;354;356;366', '38ppm', 'monochrome', 'A4', '850 feuilles', '39,9x26,3x38,2cm', 'petit groupe de travail', 'Lexmark MS410', '');
