INSERT INTO `ec_products_multilingual` VALUES ('471', '243', '1', 'MS510', '<p><span style=\"font-size:14px\"><span style=\"font-family:arial,helvetica,sans-serif\">La Lexmark MS510 se distingue par une <span style=\"color:#000080\"><strong>vitesse</strong></span>, une <span style=\"color:#000080\"><strong>fiabilit&eacute; </strong></span>et une <span style=\"color:#000080\"><strong>convivialit&eacute; </strong></span>adapt&eacute;es aux besoins d&rsquo;une entreprise.</span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:arial,helvetica,sans-serif\">Am&eacute;liorez la productivit&eacute; et gagnez du temps avec des vitesses d&rsquo;impression atteignant <span style=\"color:#000080\"><strong>42 pages par minute</strong></span>.</span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:arial,helvetica,sans-serif\">L&rsquo;imprimante est &eacute;galement <span style=\"color:#000080\"><strong>simple d&rsquo;emploi</strong></span>, gr&acirc;ce au panneau de commandes LCD&nbsp;convivial.</span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:arial,helvetica,sans-serif\">&nbsp;Une unit&eacute; de fusion &agrave; pr&eacute;chauffage instantan&eacute;, l&rsquo;impression recto verso int&eacute;gr&eacute;e, un toner ultra longue dur&eacute;e en option, ainsi que les modes Eco, Hibernation et Silence, r&eacute;duisent le bruit tout en <strong><span style=\"color:#000080\">&eacute;conomisant le papier et l&rsquo;&eacute;nergie</span></strong>.</span></span></p>\r\n', '0', '0', 'Lexmark_MS510.PDF', '<p>35S0330<br />\r\n&nbsp;</p>\r\n', '', '', '', '', '', '', '', '244', '351;353;354;356;366', '42ppm', 'monochrome', 'A4', '2000 feuilles', '39,9x26,3x38,2cm', 'petit groupe de travail', 'Lexmark MS510', '');
