INSERT INTO `ec_products_multilingual` VALUES ('479', '247', '1', 'P4015', '<p><span style=\"font-size:14px\"><span style=\"font-family:arial,helvetica,sans-serif\">L&rsquo;imprimante laserJet HP P4015n est une imprimante r&eacute;seau tr&egrave;s rapide et &eacute;volutive.</span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:arial,helvetica,sans-serif\"><span style=\"color:rgb(68, 68, 68)\">Elle dipose de&nbsp;vitesses de sortie de premi&egrave;re page &eacute;lev&eacute;e&nbsp;avec un temps d&#39;attente de </span>moins de 8,5 secondes.<span style=\"color:rgb(68, 68, 68)\">&nbsp;</span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:arial,helvetica,sans-serif\"><span style=\"color:rgb(68, 68, 68)\">La vitesse d&#39;impression&nbsp;peut atteindre le rythme de </span><span style=\"color:#000080\"><strong>50 pages &agrave; la minute</strong></span><span style=\"color:rgb(68, 68, 68)\">.</span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:arial,helvetica,sans-serif\"><font color=\"#444444\">Evolutive, elle&nbsp;</font><span style=\"color:rgb(68, 68, 68)\">peut&nbsp;recevoir jusqu&#39;&agrave; </span><span style=\"color:#000080\"><strong>6 bacs papier</strong></span><span style=\"color:rgb(68, 68, 68)\"> pour les utilisateurs ayant de gros besoin d&#39;impression.&nbsp;</span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:arial,helvetica,sans-serif\"><span style=\"color:rgb(68, 68, 68)\">Ces imprimantes peuvent recevoir des </span><span style=\"color:#000080\"><strong>trieuses </strong></span><span style=\"color:rgb(68, 68, 68)\">ou des </span><strong><span style=\"color:#000080\">chargeurs d&#39;enveloppes</span></strong><span style=\"color:rgb(68, 68, 68)\"> ainsi qu&#39;un module d&#39;agrafage. </span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:arial,helvetica,sans-serif\"><sp