INSERT INTO `ec_products_multilingual` VALUES ('481', '248', '1', 'LJ4100', '<p><span style=\"font-size:14px\"><span style=\"font-family:arial,helvetica,sans-serif\"><span style=\"color:rgb(68, 68, 68)\">L&#39;imprimante HP LaserJet 4100&nbsp;est clairement pr&eacute;vue pour les </span><span style=\"color:#000080\"><strong>gros volumes d&#39;impression</strong></span><span style=\"color:rgb(68, 68, 68)\">, tout en proposant une </span><strong><span style=\"color:#000080\">tr&egrave;s bonne qualit&eacute; d&#39;impression</span></strong><span style=\"color:rgb(68, 68, 68)\">.</span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Le temps de sortie de la 1&egrave;re page est inf&eacute;rieur &agrave; 12 secondes.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Elle dipose d&#39;une capacit&eacute; mensuelle de <span style=\"color:#000080\"><strong>150 000 pages</strong></span>, digne d&#39;une imprimante d&eacute;partementale.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Elle peut accueillir jusqu&#39;&agrave; <span style=\"color:#000080\"><strong>1600 pages en entr&eacute;e&nbsp;</strong></span>avec ses bacs suppl&eacute;mentaires.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n', '0', '0', 'HP_LJ4100.pdf', '<p>C8050A</p>\r\n', '', '', '', '', '', '', '', '247;255', '351;353;354;355;356;366', '24ppm', 'monochrome', 'A4', '1100 feuilles', '39x34,5x50,6cm', 'départementale', 'HP LJ4100', '');
