INSERT INTO `ec_products_multilingual` VALUES ('483', '249', '1', 'LJ4200', '<p><span style=\"font-family:arial,helvetica,sans-serif\"><span style=\"color:rgb(68, 68, 68)\">L&#39;imprimante HP LaserJet 4200&nbsp;est pr&eacute;vue pour les&nbsp;</span><span style=\"color:rgb(0, 0, 128)\"><strong>gros volumes d&#39;impression</strong></span><span style=\"color:rgb(68, 68, 68)\">, tout en proposant une&nbsp;</span><strong><span style=\"color:rgb(0, 0, 128)\">tr&egrave;s bonne qualit&eacute; d&#39;impression</span></strong><span style=\"color:rgb(68, 68, 68)\">.</span></span></p>\r\n\r\n<p>Son d&eacute;bit d&#39;impression est de <span style=\"color:#000080\"><strong>35&nbsp;pages par minute.</strong></span></p>\r\n\r\n<p>Elle dipose d&#39;une capacit&eacute; mensuelle de&nbsp;<span style=\"color:rgb(0, 0, 128)\"><strong>150 000 pages</strong></span>, digne d&#39;une imprimante d&eacute;partementale.</p>\r\n\r\n<p>Elle peut accueillir jusqu&#39;&agrave;&nbsp;<span style=\"color:rgb(0, 0, 128)\"><strong>1600 pages en entr&eacute;e&nbsp;</strong></span>avec ses bacs suppl&eacute;mentaires.</p>\r\n', '0', '0', 'HP-LJ4200.pdf', '<p>Q2425A<br />\r\nQ2426A</p>\r\n', '', '', '', '', '', '', '', '247;255', '351;353;354;355;356;366', '35ppm', 'monochrome', 'A4', '2600 feuilles', '41,2x36,6x43,9cm', 'départementale', 'HP LJ4200', '');
