INSERT INTO `ec_products_multilingual` VALUES ('493', '254', '1', 'T640/T642/T644', '<p><span style=\"font-size:14px\"><span style=\"font-family:arial,helvetica,sans-serif\">Les imprimantes laser&nbsp;Lexmark T640 / T642 / T644 sont&nbsp;des&nbsp;imprimantes<span style=\"color:rgb(29, 29, 37)\">&nbsp;monochromes &agrave; un prix attractif, capables d&#39;absorber les </span><span style=\"color:#000080\"><strong>gros volumes d&#39;impression</strong></span><span style=\"color:rgb(29, 29, 37)\"> des groupes de travail dynamiques.</span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:arial,helvetica,sans-serif\">Elles utilisent des consommables faciles &agrave; installer et offrent &eacute;galement de nombreuses options de gestion de papier.</span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:arial,helvetica,sans-serif\">Elles &eacute;ditent jusqu &#39;&agrave; 48<strong><span style=\"color:#000080\">&nbsp;pages par minute</span></strong>.</span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:arial,helvetica,sans-serif\">Et peuvent g&eacute;rer jusqu&#39;&agrave; <strong><span style=\"color:#000080\">4 100 feuilles</span></strong> en entr&eacute;e.</span></span></p>\r\n', '0', '0', 'Lexmark-T640-T642-T644.pdf', '<p>20G0101<br />\r\n20G0201<br />\r\n20G0301</p>\r\n', '', '', '', '', '', '', '', '255', '351;353;354;355;356;357', 'T640 35ppm - T642 43ppm - T644 48ppm', 'monochrome', 'A4', 'T640 3850 feuilles - T642/644 4100 feuilles', 'T640 42,1x34,5x49,8cm - T642/644 43,6x40,6x52,3cm', 'départementale', 'Lexmark T640/T642/T644', '');
