INSERT INTO `ec_products_multilingual` VALUES ('495', '255', '1', 'T650/T652/T654', '<p><span style=\"font-family:arial,helvetica,sans-serif\"><span style=\"font-size:14px\">Ultra-rapide, endurante et&nbsp;<span style=\"color:#000080\"><strong>polyvalente</strong></span>, la gamme de laser Lexmark T650&nbsp;s&rsquo;adapte &agrave; tous les types de besoins.</span></span></p>\r\n\r\n<p><span style=\"font-family:arial,helvetica,sans-serif\"><span style=\"font-size:14px\">Elles sont&nbsp;destin&eacute;es aux&nbsp;utilisateurs &agrave; la recherche de performances sup&eacute;rieures pour l&rsquo;impression de <span style=\"color:#000080\"><strong>gros volumes</strong></span>&nbsp;avec une capacit&eacute; allant jusqu&#39;&agrave; <strong><span style=\"color:#000080\">4300 feuilles</span></strong> ou d&rsquo;une solution efficace pour imprimer sur une<strong><span style=\"color:#000080\"> grande vari&eacute;t&eacute; de supports.</span></strong></span></span></p>\r\n\r\n<p><span style=\"font-family:arial,helvetica,sans-serif\"><span style=\"font-size:14px\">Tr&egrave;s rapides, elles peuvent &eacute;diter jusqu&#39;&agrave; <strong><span style=\"color:#000080\">53 pages par minute</span></strong>.</span></span></p>\r\n', '0', '0', 'Lexmark_T650-T652-T654.pdf', '<p>30G0102<br />\r\n30G0129<br />\r\n30G0139<br />\r\n30G0212<br />\r\n30G0202<br />\r\n30G0239<br />\r\n30G0312<br />\r\n30G0302<br />\r\n30G0339<br />\r\n30G0402<br />\r\n&nbsp;</p>\r\n', '', '', '', '', '', '', '', '244', '351;353;354;355;356;357', 'T650 43ppm - T652 48ppm - T654 53ppm', 'monochrome', 'A4', 'T650 4000 feuilles - T652/654 4300 feuilles', 'T650 42,5x34,8x49,3cm - T652 42,5x42,7x51cm - T654 42,5x43,8x51cm', 'départementale', 'Lexmark T650/T652/T654', '');
