INSERT INTO `ec_products_multilingual` VALUES ('497', '256', '1', 'T656', '<p><span style=\"font-size:14px\"><span style=\"font-family:arial,helvetica,sans-serif\">L&rsquo;imprimante Lexmark T656dne est un mix de fonctionnalit&eacute;s innovantes.</span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:arial,helvetica,sans-serif\">Elle est la premi&egrave;re imprimante monofonction A4 dot&eacute;e d&rsquo;un <span style=\"color:#000080\"><strong>grand &eacute;cran tactile couleur</strong></span> qui vous permet d&rsquo;acc&eacute;der rapidement aux solutions phare de Lexmark.</span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:arial,helvetica,sans-serif\">D&eacute;couvrez des applications puissantes qui vous aident &agrave; <span style=\"color:#000080\"><strong>gagner du temps</strong></span>, <span style=\"color:#000080\"><strong>r&eacute;duire les co&ucirc;ts</strong></span>, imprimer de mani&egrave;re responsable et communiquer plus efficacement que jamais.</span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:arial,helvetica,sans-serif\">D&eacute;di&eacute;e&nbsp;aux <strong><span style=\"color:#000080\">gros volumes d&rsquo;impression</span></strong>, elle est dot&eacute;e de pr&eacute;cieuses caract&eacute;ristiques (vitesse d&rsquo;impression, gestion polyvalente des supports, facilit&eacute; d&rsquo;utilisation imm&eacute;diate) qui confirment la valeur de la s&eacute;rie Lexmark T650.</span></span></p>\r\n', '0', '0', 'Lexmark_T656.pdf', '<p>30G0402</p>\r\n', '', '', '', '', '', '', '', '247', '351;353;354;355;356;357', '53ppm', 'monochrome', 'A4', '4300 feuilles', '42,5x43,8x52,6cm', 'départementale', 'Lexmark T656', '');
