INSERT INTO `ec_products_multilingual` VALUES ('531', '273', '1', 'C925', '<p>L&rsquo;imprimante couleur Lexmark <span style=\"color:#000080\"><strong>C925de</strong></span> r&eacute;alise des <strong><span style=\"color:#000080\">impressions couleur A3</span></strong> d&rsquo;une exceptionnelle qualit&eacute;, avec des <span style=\"color:#000080\"><strong>fonctionnalit&eacute;s simples d&rsquo;emploi</strong></span> et des solutions de productivit&eacute; innovantes et un <strong><span style=\"color:#000080\">design compact</span></strong>.</p>\r\n\r\n<p>La Lexmark<strong><span style=\"color:#000080\"> C925dte</span></strong>&nbsp;inclut en standard le recto verso et un <span style=\"color:#000080\"><strong>bac suppl&eacute;mentaire de 550 feuilles.</strong></span></p>\r\n\r\n<p>Ce mat&eacute;riel est disponible chez Eurocomputer.</p>\r\n', '0', '0', 'Lexmark_C925-A3couleur.pdf', '<p>24Z0070<br />\r\n24Z0601<br />\r\n&nbsp;</p>\r\n', '', '', '', '', '', '', '', '264', '351;353;354;355;356;366', '31ppm', 'laser couleur', 'A3', '2100 feuilles', '62,2x55,5x55,7cm', 'départementale - couleur', 'Lexmark C925', '');
