INSERT INTO `ec_products_multilingual` VALUES ('601', '308', '1', 'LQ2180', '<div>\r\n<p>Les Epson LQ-2180&nbsp;sont des&nbsp;imprimantes performantes sur le march&eacute; des imprimantes 24 aiguilles.</p>\r\n\r\n<p>Outre leur capacit&eacute; &agrave; imprimer <strong><span style=\"color:#000080\">480<strong>&nbsp;</strong>caract&egrave;res par seconde</span></strong>, elles sont&nbsp;<strong><span style=\"color:rgb(0, 0, 128)\">compactes</span></strong>, robustes et fiables.</p>\r\n\r\n<p>La&nbsp;<strong><span style=\"color:rgb(0, 0, 128)\">gestion polyvalente du papier</span></strong>&nbsp;est &eacute;galement fournie en standard pour une impression souple et rapide des commandes et factures, des &eacute;tiquettes, des journaux d&#39;&eacute;v&eacute;nements syst&egrave;me, des ch&egrave;ques et des codes barres.</p>\r\n\r\n<p>Ce mat&eacute;riel est&nbsp;<strong><span style=\"color:rgb(0, 0, 128)\">toujours disponible</span></strong>&nbsp;chez Eurocomputer.</p>\r\n\r\n<p>&nbsp;</p>\r\n</div>\r\n\r\n<div>\r\n<div style=\"text-align:center\">&nbsp;</div>\r\n</div>\r\n', '0', '0', 'Epson_LQ2180.pdf', '<p>C272001</p>\r\n', '', '', '', '', '', '', '', '294', '354;355;358', '360x180dpi', '480cps', '136 colonnes', '24 aiguilles', '69,3x26,8x40,5cm', '13kg', 'Epson LQ2180', '');
