INSERT INTO `ec_products_multilingual` VALUES ('605', '310', '1', 'FX2190', '<p>L&#39;imprimante matricielle Epson FX-2190 est con&ccedil;ue pour offrir une impression plus rapide et une meilleure efficacit&eacute; ainsi qu`une <strong><span style=\"color:#000080\">alimentation papier flexible</span></strong>, dans un design moderne et compact.</p>\r\n\r\n<p>Elle est id&eacute;ale pour les usines de fabrication, les concessionnaires automobiles, les entrep&ocirc;ts de stockage, les laboratoires, les grossistes, les transitaires, les services financiers et les utilisateurs commerciaux qui souhaitent allier de <span style=\"color:#000080\"><strong>hautes performances</strong></span> &agrave; un <strong><span style=\"color:#000080\">co&ucirc;t d`utilisation peu &eacute;lev&eacute;</span></strong>.&nbsp;</p>\r\n\r\n<p>Ce mat&eacute;riel est <strong><span style=\"color:#000080\">toujours disponible</span></strong> chez Eurocomputer.</p>\r\n', '0', '0', 'Epson_FX2190.pdf', '<p>C11C526022</p>\r\n', '', '', '', '', '', '', '', '295', '354;355;358', '240x144dpi', '680cps', '136 colonnes', '9 aiguilles', '70x27x46cm', '9kg', 'Epson FX2190', '');
