INSERT INTO `ec_products_multilingual` VALUES ('611', '313', '1', 'LQ2190', '<p>L&#39;imprimante matricielle&nbsp;Epson LQ2190 est une imprimante &agrave; impact <span style=\"color:#000080\"><strong>24 aiguilles</strong></span>, <span style=\"color:#000080\"><strong>136 colonnes</strong></span>, rapide et fiable pour les <span style=\"color:#000080\"><strong>gros volumes d&#39;impression</strong></span>.<br />\r\n<br />\r\nElle est id&eacute;ale pour l&#39;impression de factures, de listing, d&#39;&eacute;tiquettes, de documents en mutli-copies, etc.<br />\r\n<br />\r\nSa rapidit&eacute; d&#39;impression peut aller <span style=\"color:#000080\"><strong>jusqu&#39;&agrave; 576 cps</strong></span>.</p>\r\n\r\n<p>Ce mat&eacute;riel est <span style=\"color:#000080\"><strong>toujours disponible</strong></span> chez Eurocomputer.</p>\r\n', '0', '0', 'Epson_LQ2190.pdf', '<p>C11CA92001</p>\r\n', '', '', '', '', '', '', '', '294', '354;355;358', '360x180dpi', '576cps', '136 colonnes', '24 aiguilles', '69,3x26,8x40,5cm', '13kg', 'Epson LQ2190', '');
