INSERT INTO `ec_products_multilingual` VALUES ('613', '314', '1', 'DLQ3500', '<p>L&#39;imprimante matricielle &agrave; impact &agrave; plat DLQ-3500 permet de r&eacute;aliser des impressions nettes et pr&eacute;cises sur diff&eacute;rents supports jusqu&#39;&agrave; <strong><span style=\"color:#000080\">8 exemplaires</span></strong>.&nbsp;</p>\r\n\r\n<p>Imprimante matricielle rapide et robuste &agrave; <strong><span style=\"color:#000080\">24 aiguilles</span></strong> avec <strong><span style=\"color:#000080\">136 colonnes</span></strong> (10 cpi).&nbsp;</p>\r\n\r\n<p>Imprime jusqu&#39;&agrave; <strong><span style=\"color:#000080\">550 caract&egrave;res par seconde</span></strong> (High-Speed-Draft, 10 cpi).</p>\r\n\r\n<p>Ce mat&eacute;riel est <strong><span style=\"color:#000080\">toujours disponible</span></strong> chez Eurocomputer.</p>\r\n', '0', '0', 'Epson_DLQ3500.pdf', '<p>C11C396085</p>\r\n', '', '', '', '', '', '', '', '294', '354;355;358', '360x180dpi', '550cps', '136 colonnes', '24 aiguilles', '66x27,6x43,2cm', '18,1kg', 'Epson DLQ3500', '');
