INSERT INTO `ec_products_multilingual` VALUES ('635', '325', '1', 'T632', '<p><span style=\"font-size:14px\"><span style=\"font-family:arial,helvetica,sans-serif\"><span style=\"color:rgb(29, 29, 37)\">Gr&acirc;ce &agrave; son processeur rapide, l&rsquo;imprimante Lexmark T632 offre une impression </span><strong><span style=\"color:#000080\">performante </span></strong><span style=\"color:rgb(29, 29, 37)\">et </span><strong><span style=\"color:#000080\">efficace</span></strong><span style=\"color:rgb(29, 29, 37)\">. </span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:arial,helvetica,sans-serif\"><span style=\"color:rgb(29, 29, 37)\">Elle convient parfaitement comme </span><span style=\"color:#000080\"><strong>imprimante d&eacute;partementale</strong></span><span style=\"color:rgb(29, 29, 37)\">&nbsp;dans un environnement d&rsquo;impression partag&eacute;.</span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:arial,helvetica,sans-serif\"><font color=\"#1d1d25\">Elle peut &eacute;diter jusqu &#39;&agrave; </font><span style=\"color:#000080\"><strong>38&nbsp;pages par minute</strong></span><font color=\"#1d1d25\">.</font></span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:arial,helvetica,sans-serif\"><font color=\"#1d1d25\">Elle peut accepter jusqu &#39;&agrave; </font><strong><span style=\"color:#000080\">4100 feuilles</span></strong><font color=\"#1d1d25\">, en entr&eacute;e.</font></span></span></p>\r\n', '0', '0', '', '<p>4060-200</p>\r\n', '', '', '', '', '', '', '', '255', '351;353;354;355;356;357', '40ppm', 'monochrome', 'A4', '4100 feuilles', '43,6x40,6x51,3cm', 'départementale', 'Lexmark T632', '');
