INSERT INTO `ec_products_multilingual` VALUES ('637', '326', '1', 'T634', '<p><span style=\"font-size:14px\"><span style=\"font-family:arial,helvetica,sans-serif\">L&rsquo;imprimante Lexmark T634 offre une rapidit&eacute; accrue aux utilisateurs qui ont besoin d&rsquo;imprimer de <strong><span style=\"color:#000080\">tr&egrave;s gros volumes</span></strong> : une vitesse de <span style=\"color:#000080\"><strong>43 ppm</strong></span> en A4, un temps de sortie de premi&egrave;re page ultra rapide et la possibilit&eacute; d&rsquo;&eacute;voluer vers une solution multifonction.</span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:arial,helvetica,sans-serif\">Imprimante d&eacute;partementale, elle a une capacit&eacute; de<strong><span style=\"color:#000080\"> 4 100 feuilles</span></strong>.</span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:arial,helvetica,sans-serif\">Ce mat&eacute;riel est<span style=\"color:#000080\"><strong> toujours disponible </strong></span>chez Eurocomputer.</span></span></p>\r\n', '0', '0', '', '<p>4060-400</p>\r\n', '', '', '', '', '', '', '', '255', '351;353;354;355;356;357', '45ppm', 'monocrhome', 'A4', '4100 feuilles', '43,6x51,3x40,6cm', 'départementale', 'Lexmark T634', '');
