INSERT INTO `ec_products_multilingual` VALUES ('643', '329', '1', 'MS610', '<p><span style=\"font-family:arial,helvetica,sans-serif\"><span style=\"font-size:14px\">La gamme Lexmark MS610 se distingue par sa&nbsp;<span style=\"color:#000080\"><strong>vitesse</strong></span>, sa&nbsp;<span style=\"color:#000080\"><strong>fiabilit&eacute; </strong></span>et sa <strong><span style=\"color:#000080\">convivialit&eacute;</span></strong>.&nbsp;</span></span></p>\r\n\r\n<p><span style=\"font-family:arial,helvetica,sans-serif\"><span style=\"font-size:14px\">Am&eacute;liorez votre productivit&eacute; et gagnez du temps avec des vitesses d&#39;impression atteignant <strong><span style=\"color:#000080\">42 pages par&nbsp;minute</span></strong>.<span style=\"color:rgb(29, 29, 37)\">&nbsp;</span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:arial,helvetica,sans-serif\"><strong><span style=\"color:#000080\">Conviviale</span></strong>, elle dispose d&#39;un panneau de commandes avec<strong><span style=\"color:#000080\"> pav&eacute; num&eacute;rique</span></strong>.&nbsp;Vous pouvez ainsi suivre le d&eacute;roulement&nbsp;de vos t&acirc;ches sur notre &eacute;cran LCD couleur de 6 cm facile &agrave;&nbsp;lire.</span></span></p>\r\n', '0', '0', 'Lexmark-MS610dn-MS610de-MS610dte.pdf', '<p>35S0530<br />\r\n35S0430<br />\r\n35S0570</p>\r\n', '', '', '', '', '', '', '', '256;247', '351;353;354;355;356;357', '47ppm', 'monochrome', 'A4', '2300 feuilles', '39,9x30,6x38,2cm', 'bureautique ', 'Lexmark MS610', '');
