INSERT INTO `ec_products_multilingual` VALUES ('645', '330', '1', 'CS410', '<p>L&rsquo;imprimante laser couleur Lexmark CS410 offre, pour un <strong><span style=\"color:#000080\">prix abordable</span></strong>, la correspondance des couleurs PANTONE&reg; et des capacit&eacute;s d&rsquo;impression intelligente g&eacute;n&eacute;ralement disponibles uniquement sur les syst&egrave;mes de haut gamme.&nbsp;</p>\r\n\r\n<p>Vous voulez imprimer <strong><span style=\"color:#000080\">recto verso</span></strong> ? Optez pour la CS410dn.</p>\r\n\r\n<p>Vous recherchez plus de capacit&eacute; ? Optez pour la CS410dtn avec un <strong><span style=\"color:#000080\">bac suppl&eacute;mentaire</span></strong>.</p>\r\n\r\n<p>Ce mat&eacute;riel est <strong><span style=\"color:#000080\">toujours disponible</span></strong> chez Eurocomputer.</p>\r\n', '0', '0', 'Lexmark_CS410-couleur.pdf', '<p>28D0020<br />\r\n28D0070<br />\r\n28D0120</p>\r\n', '', '', '', '', '', '', '', '236', '351;353;354;355;356;366', '30ppm', 'laser couleur', 'A4', '1450 feuilles', '44,2x29,1x40,7cm', 'départementale - couleur', 'Lexmark CS410', '');
