INSERT INTO `ec_products_multilingual` VALUES ('719', '367', '1', '601xp', '<p>La 601XP&nbsp;est un mod&egrave;le durable, rapide, fiable adapt&eacute;e &agrave; un environnement difficile. Id&eacute;ale pour des applications d&#39;&eacute;tiquetage exigeante et &agrave; fort volume. Elle permet aussi des impressions de grande qualit&eacute; pouvant aller jusqu&#39;&agrave; 300 dpi.</p>\r\n', '0', '0', 'Intermec-601xp.pdf', '<p>G6X01010743000<br />\r\nG6X01000000000<br />\r\nG6X01000003000<br />\r\n&nbsp;imprimante intermec 601XP</p>\r\n', '', '', '', '', '', '', '', '78;26;61', '109;110;111;112;113;115;114;116', '300dpi', '225mm/s', '165,3mm', '15kg', '335x239x482mm', 'industrielle', 'Intermec 601xp', '<p>G6X01010743000<br />\r\nG6X01000000000<br />\r\nG6X01000003000<br />\r\n&nbsp;</p>\r\n\r\n<p><a href=\"https://www.eurocomputer.fr/imprimantes-etiquettes/intermec/gamme-industrielle/px4i-77.html\">intermec PX4i</a></p>\r\n\r\n<p><a href=\"https://www.eurocomputer.fr/imprimantes-etiquettes/intermec/gamme-industrielle/px6i-78.html\">intermec PX6i</a></p>\r\n');
