INSERT INTO `ec_products_multilingual` VALUES ('721', '368', '1', '90', '<p style=\"text-align:justify\">Connect&eacute;e &agrave; un r&eacute;seau standard (Ethernet TCP/IP) ou <span style=\"color:#000080\"><strong>Wifi </strong></span>(r&eacute;seau sans fil 802.11), la platine Axel 90 permet d&#39;acc&eacute;der aux applications textes et graphiques ex&eacute;cut&eacute;es sur diff&eacute;rents serveurs : Windows, Unix/Linux,AS/400, S/390...&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">Ce mod&egrave;le offre le support du <span style=\"color:#000080\"><strong>double &eacute;cran</strong></span> et de tr&egrave;s hautes performances d&#39;affichage.</p>\r\n\r\n<p style=\"text-align:justify\">Avec son format <span style=\"color:#000080\"><strong>ultra compact</strong></span>, la platine Axel 90 a &eacute;t&eacute; con&ccedil;ue pour &ecirc;tre fix&eacute;e derri&egrave;re un &eacute;cran plat.</p>\r\n', '0', '0', 'Axel-90.pdf', '<p>AX3-90</p>\r\n', '', '', '', '', '', '', '', '211', '', '-', 'ports ethernet, USB, sortie vidéo VGA, DisplayPort', 'en option', '-', '166x40x150mm', '0,61kg', 'CLIENTS LÉGERS AXEL 90', '');
