INSERT INTO `ec_products_multilingual` VALUES ('723', '369', '1', 'D10D', '<p style=\"text-align:justify\">Polyvalent, <span style=\"color:#000080\"><strong>compact</strong></span> et efficace, ce client l&eacute;ger double coeur haute performance de milieu de gamme permet d&rsquo;acc&eacute;der &agrave; une grande vari&eacute;t&eacute; d&rsquo;applications incluant notamment les applications<strong><span style=\"color:#000080\"> 3D</span></strong> et<strong><span style=\"color:#000080\"> HD</span></strong>.</p>\r\n\r\n<p style=\"text-align:justify\">Le D10D est &eacute;quip&eacute; de Wyse <span style=\"color:#000080\"><strong>ThinOS</strong></span>, syst&egrave;me d&#39;exploitation <strong><span style=\"color:#000080\">ultrarapide</span></strong>, con&ccedil;u pour les environnements hautement s&eacute;curis&eacute;s.&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">Ils remplacent au catalogue les <a href=\"http://www.eurocomputer.fr/micro/clients-legers-dell-wyse/clients-legers/v10-204.html\"><span style=\"color:#000080\"><u><strong>Wyse V10</strong></u></span></a> toujours disponibles chez Eurocomputer.</p>\r\n', '0', '0', 'Wyse-D10.pdf', '<p>909838-02L<br />\r\n909838-10L<br />\r\n909649-02L<br />\r\n909648-02L<br />\r\n909648-72L</p>\r\n\r\n<p>&nbsp;</p>\r\n', '', '', '', '', '', '', '', '196;216', '', '2Gb / 2Gb', 'ports ethernet, USB, DVI, DisplayPort, port fibre NIC en option', 'en option double antennes', 'ThinOS', '170x40x185mm', '0,93kg', 'CLIENTS LÉGERS DELL WYSE D10D', '');
