INSERT INTO `ec_products_multilingual` VALUES ('725', '370', '1', 'Metrologic MS9535', '<p>La douchette sans fil Metrologic MS9535 est un lecteur simple, abordable et fiable.</p>\r\n\r\n<p>Cette douchette <span style=\"color:#000080\"><strong>Bluetooth </strong></span>est <span style=\"color:#000080\"><strong>tr&egrave;s simple &agrave; configurer</strong></span>. Pour synchroniser le lecteur et la base, il suffit d&#39;une simple lecture du code-barre pr&eacute;sent sur la base, et le lecteur fonctionne.</p>\r\n\r\n<p>Sa <span style=\"color:#000080\"><strong>port&eacute;e de lecture s&#39;&eacute;tend jusqu&#39;&agrave; 20cm</strong></span>.</p>\r\n\r\n<p>D&eacute;clin&eacute; avec une interface USB, RS232 ou clavier (Wedge), ce mat&eacute;riel est disponible chez Eurocomputer.</p>\r\n', '0', '0', 'Honeywell-Metrologic-MS9535.pdf', '<p>MK9535-39C538<br />\r\nMK9535-39B540<br />\r\nMS9535-5M-3<br />\r\nMS9535-5-3<br />\r\nMS9535-5<br />\r\nMK9535-79C538<br />\r\nMK9535-79B5M47<br />\r\nMK9535-79B5M38<br />\r\nMK9535-79B548<br />\r\nMK9535-79B547<br />\r\nMK9535-79B540<br />\r\nMK9535-79B538<br />\r\nMK9535-79B514<br />\r\nMK9535-79B511<br />\r\nMK9535-79A540<br />\r\nMK9535-538/HCA<br />\r\nMK9535-39C547<br />\r\nMK9535-39B5M47<br />\r\nMK9535-39B5M38<br />\r\nMK9535-39B5M14<br />\r\nMK9535-39B548<br />\r\nMK9535-39B547<br />\r\nMK9535-39B538<br />\r\nMK9535-39B514<br />\r\nMK9535-39B511</p>\r\n', '', '', '', '', '', '', '', '166', '344;345;346;347;348', 'sans fil', 'USB, clavier Wedge, série RS232', '1D', '25cm', '78x198x40mm', '149g', 'Intermec / Honeywell Metrologic MS9535', '');
