INSERT INTO `ec_products_multilingual` VALUES ('739', '377', '1', 'LJ4250', '<p><span style=\"font-family:arial,helvetica,sans-serif\"><span style=\"color:rgb(68, 68, 68)\">L&#39;imprimante HP LaserJet 4250&nbsp;est pr&eacute;vue pour les&nbsp;</span><span style=\"color:rgb(0, 0, 128)\"><strong>gros volumes d&#39;impression</strong></span><span style=\"color:rgb(68, 68, 68)\">, tout en proposant une&nbsp;</span><strong><span style=\"color:rgb(0, 0, 128)\">tr&egrave;s bonne qualit&eacute; d&#39;impression</span></strong><span style=\"color:rgb(68, 68, 68)\">.</span></span></p>\r\n\r\n<p>Son d&eacute;bit d&#39;impression est de <span style=\"color:#000080\"><strong>4<strong>5&nbsp;</strong>pages par minute.</strong></span></p>\r\n\r\n<p>Elle dipose d&#39;une capacit&eacute; mensuelle de <span style=\"color:#000080\"><strong>200&nbsp;000 pages</strong></span>, digne d&#39;une imprimante d&eacute;partementale.</p>\r\n\r\n<p>Elle peut accueillir jusqu&#39;&agrave; <span style=\"color:#000080\"><strong>3100&nbsp;pages en entr&eacute;e&nbsp;</strong></span>avec ses bacs suppl&eacute;mentaires.</p>\r\n', '0', '0', 'laser-hp-4250.pdf', '<p>Q5400A<br />\r\nQ5401A<br />\r\nQ2402A<br />\r\nQ5403A<br />\r\nQ5404A</p>\r\n', '', '', '', '', '', '', '', '247;255', '351;353;354;355;356;366', '43ppm', 'monochrome', 'A4', '3100 feuilles', '418x377x451mm', 'départementale', 'HP LJ4250', '');
