INSERT INTO `ec_products_multilingual` VALUES ('771', '393', '1', 'PC41', '<p>Les imprimantes bureau PC41 disposent d&#39;un encombrement r&eacute;duit <span style=\"color:#000080\"><strong>id&eacute;al dans un espace limit&eacute;</strong></span>.<br />\r\n<br />\r\nElles sont con&ccedil;ues d&#39;une <span style=\"color:#000080\"><strong>double coque</strong></span> pour un <span style=\"color:#000080\"><strong>chargement des consommables simple et intuitif</strong></span>.<br />\r\n<br />\r\nDot&eacute;es d&#39;une r&eacute;solution de 203dpi, elles editent des &eacute;tiquettes jusqu&#39;&agrave; <span style=\"color:#000080\"><strong>108mm de large</strong></span>.<br />\r\n<br />\r\nCe mat&eacute;riel est <span style=\"color:#000080\"><strong>toujours disponible</strong></span> chez Eurocomputer.</p>\r\n', '0', '0', 'Intermec-PC41.pdf,', '<p>PC41A000000<br />\r\nPC41A002000<br />\r\nPC41A001100<br />\r\nPC41A001000</p>\r\n\r\n<p>IMPRIMANTE INTERMEC PC41</p>\r\n', '', '', '', '', '', '', '', '376;12', '109;110;112;113;115;114;116', '203dpi', '101,6mm/s', '108mm', '2,3kg', '230x167x257mm', 'bureautique', 'Intermec PC41', '<p>PC41A000000<br />\r\nPC41A002000<br />\r\nPC41A001100<br />\r\nPC41A001000</p>\r\n\r\n<p><a href=\"https://www.eurocomputer.fr/imprimantes-etiquettes/intermec/petits-modeles/pc43-376.html\">intermec PC43</a></p>\r\n\r\n<p><a href=\"https://www.eurocomputer.fr/imprimantes-etiquettes/intermec/petits-modeles/pf8-20.html\">intermec PF8</a></p>\r\n');
