INSERT INTO `ec_products_multilingual` VALUES ('777', '396', '1', 'T10D', '<p style=\"text-align:justify\">Les clients l&eacute;gers de la gamme T sont des postes d&#39;entr&eacute;e de gamme&nbsp;<strong><span style=\"color:rgb(0, 0, 128)\">compacts</span></strong>, <span style=\"color:#000080\"><strong>bon march&eacute;</strong></span>&nbsp;et&nbsp;<strong><span style=\"color:rgb(0, 0, 128)\">flexibles</span></strong>.</p>\r\n\r\n<p style=\"text-align:justify\">Le T10 est propos&eacute; sous<strong>&nbsp;</strong>ThinOS qui permet un <strong><span style=\"color:#000080\">d&eacute;marrage</span></strong>&nbsp;<span style=\"color:rgb(0, 0, 128)\"><strong>instantan&eacute;</strong></span>, une configuration centralis&eacute;e&nbsp;facilit&eacute;e, et une&nbsp;<strong><span style=\"color:rgb(0, 0, 128)\">s&eacute;curit&eacute;&nbsp;</span></strong>totale.&nbsp;Par rapport au<strong><span style=\"color:#000080\">&nbsp;</span><a href=\"http://www.eurocomputer.fr/micro/clients-legers-dell-wyse/clients-legers/t10-197.html\"><span style=\"color:#000080\"><u>T10</u></span></a></strong>, il dispose en plus de <span style=\"color:#000080\"><strong>deux sorties vid&eacute;o</strong></span> DVI.</p>\r\n\r\n<p style=\"text-align:justify\">Ce mat&eacute;riel est&nbsp;<span style=\"color:rgb(0, 0, 128)\"><strong>toujours disponible</strong></span>&nbsp;chez Eurocomputer.</p>\r\n', '0', '0', 'Wyse-T10D.pdf', '<p>909641-02L<br />\r\n909641-01L</p>\r\n', '', '', '', '', '', '', '', '196;197;216', '', '4Go / 2Go', 'ports ethernet, USB, double vidéo : DVI-I et DVI-D', 'en option double antenne Wifi', 'ThinOS', '176x35x121mm', '0,5kg', 'CLIENTS LÉGERS DELL WYSE T10D', '');
