INSERT INTO `ec_products_multilingual` VALUES ('779', '397', '1', 'T420', '<p>Le HP T420 &nbsp;est un client l&eacute;ger <span style=\"color:#000080\"><strong>abordable</strong></span> qui offre une fiabilit&eacute; digne de confiance et qui facilite le d&eacute;ploiement et l&#39;utilisation.</p>\r\n\r\n<p>De format <span style=\"color:#000080\"><strong>ultra compact</strong></span>, il peut se placer derri&egrave;re un &eacute;cran avec le support VESA en option.</p>\r\n\r\n<p>Il est propos&eacute; sous HP Thin Pro, HP Smart Zero ou <strong><span style=\"color:#000080\">Windows Embedded Standard 7E</span></strong>.</p>\r\n\r\n<p>Ce mat&eacute;riel est <span style=\"color:#000080\"><strong>toujours disponible</strong></span> chez Eurocomputer.</p>\r\n', '0', '0', 'hp-t420.pdf', '<p>M5R76AT<br />\r\nM5R74AT<br />\r\nM5R72AT<br />\r\nM5R74AA<br />\r\nM5R73AA<br />\r\nMRR72AA<br />\r\nM5R75AA<br />\r\nM5R76AA<br />\r\nM5R73AT<br />\r\nM5R75AT</p>\r\n', '', '', '', '', '', '', '', '374;219', '', '8Go / 2Go', 'ports ethernet, USB, DVI, sortie vidéo VGA', 'en option', 'HP ThinPro, Smart Zero', '180x35x140mm', '0,74kg', 'CLIENTS LÉGERS HP T420', '');
