INSERT INTO `ec_products_multilingual` VALUES ('785', '400', '1', 'Eclipse 5145', '<p style=\"text-align:justify\">La&nbsp;<span style=\"color:rgb(0, 0, 128)\"><strong>douchette code-barre Honeywell Eclipse 5145</strong></span>&nbsp;&nbsp;est un mod&egrave;le<strong>&nbsp;filaire</strong>&nbsp;haut de gamme.</p>\r\n\r\n<p style=\"text-align:justify\">Gr&acirc;ce &agrave;&nbsp;son<span style=\"color:rgb(0, 0, 128)\"><strong>&nbsp;bouton central&nbsp;</strong></span>vous acc&eacute;der &agrave; toutes les applications, le&nbsp;<span style=\"color:rgb(0, 0, 128)\"><strong>scanner code-barres Honeywelle 5145</strong></span>&nbsp;est donc extr&ecirc;mement simple d&#39;utilisation.&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">La douchette 5145 est dot&eacute;e d&#39;une technologie&nbsp;<span style=\"color:rgb(0, 0, 128)\"><strong>CodeGate Honeywell</strong></span>, c&#39;est &agrave; dire qu&#39;en appuyant sur le bouton central en visant le code-barre,&nbsp;l&#39;utilisateur peut transmettre directement les donn&eacute;es.</p>\r\n\r\n<p style=\"text-align:justify\">Le<span style=\"color:rgb(0, 0, 128)\"><strong>&nbsp;scanner code-barre Honeywell Eclipse 5145</strong></span>&nbsp;est donc adapt&eacute; &agrave; de nombreuses activit&eacute;s (<span style=\"color:rgb(0, 0, 128)\"><strong>gestions des stocks, gestion de points de vente, colisages</strong></span>...).</p>\r\n\r\n<p style=\"text-align:justify\">A not&eacute; que la<span style=\"color:rgb(0, 0, 128)\"><strong>&nbsp;ligne de blayage&nbsp;</strong></span>de lecteur Honeywell 5145 s&#39;&eacute;largit &agrave; mesure que le scanner s&#39;&eacute;loigne du code-barres.&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">L&#39;autre technologie pr&eacute;sente sur ce lecteur code-barre est le&nbsp;<span style=\"color:rgb(0, 0, 128)\"><strong>CodeSense</strong></span>&nbsp;qui permet au lecteur de passer du<strong>&nbsp;<span style=\"color:rgb(0, 0, 128)\">mode veille au mode actif</span></strong>&nbsp;d&eacute;s que celui-ci d&eacute;tecte un code.&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n', '0', '0', 'Honeywell-MS5145.pdf', '<p>MK5145-31A38-EU<br />\r\nMK5145-31A47-EU&nbsp;<br />\r\nMK5145-31C41-EU&nbsp;<br />\r\nMK5145-32A38-EU&nbsp;<br />\r\nMK5145-32C41-EU&nbsp;<br />\r\nMK5145-71A38-EU&nbsp;<br />\r\nMK5145-71C41-EU&nbsp;<br />\r\nMS5145-11-3-EU&nbsp;<br />\r\nMS5145-11-EU&nbsp;<br />\r\nMS5145-38-3-EU&nbsp;<br />\r\nMS5145-38-EU&nbsp;<br />\r\nMS5145-40-3-EU&nbsp;<br />\r\nMS5145-40-EU&nbsp;<br />\r\nMS5145-41-3-EU&nbsp;<br />\r\nMS5145-41-EU&nbsp;<br />\r\nMS5145-47-3-EU&nbsp;<br />\r\nMS5145-47-EU&nbsp;</p>\r\n\r\n<p>Douchette MS5145</p>\r\n\r\n<p>Douchette 5145&nbsp;</p>\r\n\r\n<p>Douchette MK5145</p>\r\n\r\n<p>Lecteur MS5145</p>\r\n\r\n<p>Lecteur MK5145</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n', '', '', '', '', '', '', '', '157;160', '344;345;346;347;348', 'filaire', 'USB, clavier Wedge, série RS232', '1D', '17,8cm', '63x169x51mm (position verticale)', '100g', 'Intermec / Honeywell Eclipse 5145', '<p>MK5145-31A38-EU<br />\r\nMK5145-31A47-EU&nbsp;<br />\r\nMK5145-31C41-EU&nbsp;<br />\r\nMK5145-32A38-EU&nbsp;<br />\r\nMK5145-32C41-EU&nbsp;<br />\r\nMK5145-71A38-EU&nbsp;<br />\r\nMK5145-71C41-EU&nbsp;<br />\r\nMS5145-11-3-EU&nbsp;<br />\r\nMS5145-11-EU&nbsp;<br />\r\nMS5145-38-3-EU&nbsp;<br />\r\nMS5145-38-EU&nbsp;<br />\r\nMS5145-40-3-EU&nbsp;<br />\r\nMS5145-40-EU&nbsp;<br />\r\nMS5145-41-3-EU&nbsp;<br />\r\nMS5145-41-EU&nbsp;<br />\r\nMS5145-47-3-EU&nbsp;<br />\r\nMS5145-47-EU&nbsp;</p>\r\n\r\n<p><strong><a href=\"http://www.eurocomputer.fr/scanners-code-barres/intermec-honeywell/douchette-filaire/sg10-163.html\">Intermec/Honeywell SG10</a></strong></p>\r\n\r\n<p><strong><a href=\"http://www.eurocomputer.fr/scanners-code-barres/intermec-honeywell/douchette-filaire/sg20-2d-164.html\">Intermec/Honeywell SG20</a></strong></p>\r\n');
