INSERT INTO `ec_products_multilingual` VALUES ('799', '407', '1', 'MS811/MS812', '<p><span style=\"font-family:arial,helvetica,sans-serif\"><span style=\"font-size:14px\"><span style=\"color:rgb(29, 29, 37)\">L&#39;imprimante laser Lexmark MS811dn est une imprimante d&eacute;partementale que vous pouvez connecter&nbsp;en r&eacute;seau, dot&eacute;e de l&#39;impression </span><span style=\"color:#000080\"><strong>recto verso</strong></span><span style=\"color:rgb(29, 29, 37)\"> interne, d&#39;un processeur &agrave; double c&oelig;ur 800&nbsp;MHz et d&#39;une m&eacute;moire de 512&nbsp;Mo pour atteindre une </span><strong><span style=\"color:#000080\">vitesse d&#39;impression de 60&nbsp;ppm</span></strong><span style=\"color:rgb(29, 29, 37)\">.</span></span></span></p>\r\n\r\n<p><font color=\"#1d1d25\" face=\"arial, helvetica, sans-serif\"><span style=\"font-size:14px\">Imprimante gros volume, elle peut &eacute;diter jusqu&#39;&agrave; </span></font><span style=\"color:#000080\"><font face=\"arial, helvetica, sans-serif\"><span style=\"font-size:14px\"><strong>75 000 pages par mois.</strong></span></font></span></p>\r\n\r\n<p><font face=\"arial, helvetica, sans-serif\"><span style=\"font-size:14px\">Elle dispose d&#39;une capacit&eacute; d&#39;alimentation maximale de <span style=\"color:#000080\"><strong>4 400 feuilles.</strong></span></span></font></p>\r\n', '0', '0', 'Lexmark-MS811-MS812.pdf', '<p>40G0230<br />\r\n40G0450<br />\r\n40G0220<br />\r\n40G0360<br />\r\n40G0330<br />\r\n40G0480</p>\r\n', '', '', '', '', '', '', '', '255;254', '351;353;354;355;356;366', 'MS811 60ppm - MS812 66ppm', 'monochrome', 'A4', '4400 feuilles', '42,5x41,9x51cm', 'départementale', 'Lexmark MS811/MS812', '');
