INSERT INTO `ec_products_multilingual` VALUES ('801', '408', '1', 'GT800', '<p style=\"text-align:justify\">De par ses remarquables performances, son extr&ecirc;me <span style=\"color:#000080\"><strong>fiabilit&eacute; </strong></span>et son <strong><span style=\"color:#000080\">prix comp&eacute;titif</span></strong>, l&rsquo;imprimante bureau thermique direct/transfert thermique&nbsp;GT800 propose une multitude de fonctionnalit&eacute;s avanc&eacute;es pour r&eacute;pondre &agrave; diverses applications d&rsquo;impression &agrave; <span style=\"color:#000080\"><strong>faible ou moyen volume</strong></span>.</p>\r\n\r\n<p style=\"text-align:justify\">Avec son design <span style=\"color:#000080\"><strong>compact </strong></span>et <span style=\"color:#000080\"><strong>solide</strong></span>, elle convient parfaitement aux <span style=\"color:#000080\"><strong>environnements commerciaux</strong></span> dont les besoins d&#39;impression sont mod&eacute;r&eacute;s.</p>\r\n\r\n<p style=\"text-align:justify\">Ce mat&eacute;riel est disponible chez Eurocomputer.</p>\r\n', '0', '0', 'Zebra-GT800-FR.pdf', '<p>GT800-100520-100<br />\r\nGT800-100521-100<br />\r\nGT800-100522-100<br />\r\nGT800-100420-100<br />\r\nGT800-100421-100<br />\r\nGT800-100422-100<br />\r\nGT800-300520-100<br />\r\nGT800-300521-100<br />\r\nGT800-300522-100<br />\r\nGT800-300420-100<br />\r\nGT800-300421-100<br />\r\nGT800-300422-100</p>\r\n\r\n<p><br />\r\n&nbsp;Imprimante zebra GT800</p>\r\n', '', '', '', '', '', '', '', '', '109;110;112;116', '203 ou 300dpi', '127mm/s', '104mm', '5kg', '197x184x273mm', 'bureautique', 'Zebra GT800', '<p>GT800-100520-100<br />\r\nGT800-100521-100<br />\r\nGT800-100522-100<br />\r\nGT800-100420-100<br />\r\nGT800-100421-100<br />\r\nGT800-100422-100<br />\r\nGT800-300520-100<br />\r\nGT800-300521-100<br />\r\nGT800-300522-100<br />\r\nGT800-300420-100<br />\r\nGT800-300421-100<br />\r\nGT800-300422-100</p>\r\n\r\n<p><a href=\"https://www.eurocomputer.fr/imprimantes-eti