INSERT INTO `ec_products_multilingual` VALUES ('803', '409', '1', 'MX310', '<p><span style=\"font-size:14px\"><span style=\"font-family:arial,helvetica,sans-serif\">L&#39;imprimate laser Lexmark&nbsp;<span style=\"color:#000080\"><strong>multifonction&nbsp;</strong></span>MX310 est <span style=\"color:#000080\"><strong>rapide </strong></span>avec ses vitesses de traitement adapt&eacute;es aux besoins des entreprises.</span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:arial,helvetica,sans-serif\">Elle s&rsquo;acquitte rapidement les t&acirc;ches d&rsquo;impression, de copie, de t&eacute;l&eacute;copie et de num&eacute;risation.</span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:arial,helvetica,sans-serif\">La <span style=\"color:#000080\"><strong>gestion efficace du papier </strong></span>vous aide &agrave; am&eacute;liorer votre productivit&eacute; et &agrave; &eacute;conomiser les ressources.</span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:arial,helvetica,sans-serif\">Profitez de vitesses d&rsquo;impression atteignant <span style=\"color:#000080\"><strong>33 pages par minute</strong></span> avec impression ou copie de la premi&egrave;re page en 6,5 secondes seulement.</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n', '0', '0', 'Lexmark_MX310.pdf', '<p>35S5745</p>\r\n', '', '', '', '', '', '', '', '', '351;353;354;355;356;366', '33 ppm', 'monochrome ', 'A4', '850 feuilles', '39,9x46,8x47,2cm', 'multifonction : imprimante, scanner, fax, photocopieuse', 'Lexmark MX310', '');
