INSERT INTO `ec_products_multilingual` VALUES ('865', '442', '1', 'X945e', '<p><span style=\"font-size:14px\"><span style=\"font-family:arial,helvetica,sans-serif\"><span style=\"color:rgb(29, 29, 37)\">L&#39;infatigable multifonction couleur Lexmark X945e pour les impressions </span><strong><span style=\"color:#000080\">A3</span></strong><span style=\"color:rgb(29, 29, 37)\"> vous apporte la qualit&eacute;, la vitesse et la </span><span style=\"color:#000080\"><strong>polyvalence </strong></span><span style=\"color:rgb(29, 29, 37)\">n&eacute;cessaires &agrave; la r&eacute;alisation de tous vos documents en interne.</span></span></span></p>\r\n\r\n<p><font color=\"#1d1d25\" face=\"arial, helvetica, sans-serif\"><span style=\"font-size:14px\">Imprimante </span></font><span style=\"color:#000080\"><font face=\"arial, helvetica, sans-serif\"><span style=\"font-size:14px\"><strong>multifonction</strong></span></font></span><font color=\"#1d1d25\" face=\"arial, helvetica, sans-serif\"><span style=\"font-size:14px\">, elle combine impression laser couleur,&nbsp;photocopie couleur,&nbsp;t&eacute;l&eacute;copie et&nbsp;scanner couleur.</span></font></p>\r\n\r\n<p><font color=\"#1d1d25\" face=\"arial, helvetica, sans-serif\"><span style=\"font-size:14px\">Ce mat&eacute;riel est </span></font><strong><span style=\"color:#000080\"><font face=\"arial, helvetica, sans-serif\"><span style=\"font-size:14px\">toujours disponible</span></font></span></strong><font color=\"#1d1d25\" face=\"arial, helvetica, sans-serif\"><span style=\"font-size:14px\"> chez Eurocomputer.</span></font></p>\r\n\r\n<p>&nbsp;</p>\r\n', '0', '0', 'Lexmark_X945e.pdf', '<p>21Z0221</p>\r\n', '', '', '', '', '', '', '', '', '351;353;354;355;356;366', '45ppm', 'multifonction laser couleur ', 'A3/A4/A5', '3140 feuilles', '650x1153x810mm', 'départementale multifonction : imprimante, copieur, scanner, télécopieur', 'Lexmark X945e', '');
