INSERT INTO `ec_products_multilingual` VALUES ('871', '445', '1', 'QLn320', '<p>L&#39;imprimante portable QLN320 gagne en l&eacute;g&egrave;ret&eacute; et en r&eacute;sistance gr&acirc;ce &agrave; un nouvel affichage int&eacute;gr&eacute; en <span style=\"color:#000080\"><strong>verre tremp&eacute; monobloc</strong></span> et une <strong><span style=\"color:#000080\">surcoque moul&eacute;e en caoutchouc</span></strong>.<br />\r\n<br />\r\nSon affichage est plus large et plus lisible, avec une <span style=\"color:#000080\"><strong>navigation plus simple</strong></span>. Des accessoires pratiques facilitent le chargement de la batterie.<br />\r\n<br />\r\n<span style=\"color:#000080\"><strong>Mobile</strong></span>, elle dispose du r&eacute;seau LAN sans fil 802.11b ou du Bluetooth.<br />\r\n<br />\r\nElle est la solution id&eacute;ale pour des secteurs comme la commerce et la distribution, la sant&eacute; et les transports / logistique.<br />\r\n<br />\r\nDisposant d&#39;une r&eacute;solution de 203dpi, elle permet d&#39;&eacute;diter des &eacute;tiquettes jusqu&#39; &agrave; <span style=\"color:#000080\"><strong>72mm de large</strong></span>.</p>\r\n\r\n<p>Ce mat&eacute;riel&nbsp;disponible chez Eurocomputer remplace au catalogue les <a href=\"http://www.eurocomputer.fr/imprimantes-etiquettes/zebra/imprimantes-portables/ql320--444.html\"><span style=\"color:#000080\"><strong><u>QL320+</u></strong></span></a>.</p>\r\n', '0', '0', 'Zebra_QLn220-320.pdf', '<p>QN3-AU1AEM11-00<br />\r\nQN3-AUCAEM11-00<br />\r\nQN3-AUNAEM11-00<br />\r\nQN3-AUNBEM11-00<br />\r\nQN3-AUNAEMC1-00</p>\r\n\r\n<p>imprimante zebra QLn320</p>\r\n', '', '', '', '', '', '', '', '443', '109;110;112;113;115;114;116', '203dpi', '100mm/s', '72mm', '0,72kg', '117x173x84mm', 'mobile', 'Zebra QLn320', '<p>QN3-AU1AEM11-00<br />\r\nQN3-AUCAEM11-00<br />\r\nQN3-AUNAEM11-00<br />\r\nQN3-AUNBEM11-00<br />\r\nQN3-AUNAEMC1-00</p>\r\n\r\n<p><a href=\"https://www.eurocomputer.fr/imprimantes-etiquettes/zebra/imprima