INSERT INTO `ec_products_multilingual` VALUES ('889', '454', '1', 'M506', '<p><span style=\"font-size:14px\"><span style=\"font-family:arial,helvetica,sans-serif\"><span style=\"color:rgb(68, 68, 68)\">Les imprimantes laser HP M506 sont </span>rapides<span style=\"color:rgb(68, 68, 68)\">, elles d&eacute;marrent&nbsp;imm&eacute;diatement et </span><span style=\"color:#000080\"><strong>&eacute;conomisent l&#39;&eacute;nergie</strong></span><span style=\"color:rgb(68, 68, 68)\">.&nbsp;</span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:arial,helvetica,sans-serif\"><span style=\"color:rgb(84, 84, 84)\">Imprimez votre premi&egrave;re page en</span><span style=\"color:#000080\"><strong> 8,5 secondes</strong></span><span style=\"color:rgb(84, 84, 84)\"> seulement depuis le mode veille qui permet d&#39;&eacute;conomiser de l&#39;&eacute;nergie.</span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Sa vitesse d&#39;impression peut atteindre <strong><span style=\"color:#000080\">43 pages par minute.</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:arial,helvetica,sans-serif\"><span style=\"color:rgb(84, 84, 84)\">D&eacute;sormais </span><span style=\"color:#000080\"><strong>20 % plus petite</strong></span><span style=\"color:rgb(84, 84, 84)\">, cette imprimante trouve sa place dans des endroits exigus et offre un fonctionnement </span><span style=\"color:#000080\"><strong>silencieux</strong></span><span style=\"color:rgb(84, 84, 84)\">.</span></span></span></p>\r\n', '0', '0', 'HP-M506.pdf', '<p>lF2A68A<br />\r\nF2A69A<br />\r\nF2A70A</p>\r\n', '', '', '', '', '', '', '', '244', '351;353;354;355;356;366', '43ppm', 'monochrome', 'A4', '2300 feuilles', '41,8x29,8x37,6cm', 'départementale', 'HP M506', '');
