INSERT INTO `ec_products_multilingual` VALUES ('895', '457', '1', 'P4515', '<p><span style=\"font-family:arial,helvetica,sans-serif\">L&rsquo;imprimante laserJet HP P4515n est une imprimante r&eacute;seau tr&egrave;s rapide et &eacute;volutive.</span></p>\r\n\r\n<p><span style=\"font-family:arial,helvetica,sans-serif\"><span style=\"color:rgb(68, 68, 68)\">Elle dipose de&nbsp;vitesses de sortie de premi&egrave;re page &eacute;lev&eacute;e&nbsp;avec un temps d&#39;attente de&nbsp;</span>moins de 8,5 secondes.<span style=\"color:rgb(68, 68, 68)\">&nbsp;</span></span></p>\r\n\r\n<p><span style=\"font-family:arial,helvetica,sans-serif\"><span style=\"color:rgb(68, 68, 68)\">La vitesse d&#39;impression&nbsp;peut atteindre le rythme de</span><span style=\"color:#000080\"><strong> 62&nbsp;pages &agrave; la minute</strong></span><span style=\"color:rgb(68, 68, 68)\">.</span></span></p>\r\n\r\n<p><span style=\"font-family:arial,helvetica,sans-serif\">Evolutive, elle&nbsp;<span style=\"color:rgb(68, 68, 68)\">peut&nbsp;recevoir jusqu&#39;&agrave;&nbsp;</span><span style=\"color:rgb(0, 0, 128)\"><strong>6 bacs papier</strong></span><span style=\"color:rgb(68, 68, 68)\">&nbsp;pour les utilisateurs ayant de gros besoin d&#39;impression.&nbsp;</span></span></p>\r\n\r\n<p><span style=\"font-family:arial,helvetica,sans-serif\"><span style=\"color:rgb(68, 68, 68)\">Ces imprimantes peuvent recevoir des&nbsp;</span><span style=\"color:rgb(0, 0, 128)\"><strong>trieuses&nbsp;</strong></span><span style=\"color:rgb(68, 68, 68)\">ou des&nbsp;</span><strong><span style=\"color:rgb(0, 0, 128)\">chargeurs d&#39;enveloppes</span></strong><span style=\"color:rgb(68, 68, 68)\">&nbsp;ainsi qu&#39;un module d&#39;agrafage.</span></span></p>\r\n\r\n<p><span style=\"font-family:arial,helvetica,sans-serif\"><span style=\"color:rgb(68, 68, 68)\">L&#39;impression&nbsp;</span><span style=\"color:rgb(0, 0, 128)\"><strong>recto-verso</strong></span><span style=\"color:rgb(68, 68, 68)\">&nbsp;est disponible en standard sur les mod&egrave;les &quot;dn&quot; et &quot;x&quot;</span></span></p>\r\n', '0', '0', 'HP_P4515.pdf', '<p>CB514A<br />\r\nCB515A<br />\r\nCB516A</p>\r\n', '', '', '', '', '', '', '', '247', '351;353;354;355;356;366', '62ppm', 'monochrome', 'A4', '3600 feuilles', '41,9x39,3x45cm (minimum)', 'départementale', 'HP P4515', '');
