INSERT INTO `ec_products_multilingual` VALUES ('897', '458', '1', 'CK3X', '<p><span style=\"font-family:verdana,geneva,sans-serif\"><span style=\"font-size:12px\">Le terminal code-barres CK3X&nbsp;convient &agrave; une utilisation en&nbsp;</span><span style=\"color:rgb(0, 0, 128); font-size:12px\"><strong>environnements difficiles</strong></span><span style=\"font-size:12px\">&nbsp;puisqu&rsquo;il b&eacute;n&eacute;ficie d&rsquo;un indice de protection &eacute;lev&eacute; et r&eacute;siste aux chutes r&eacute;p&eacute;t&eacute;es.&nbsp;</span></span></p>\r\n\r\n<p><span style=\"font-family:verdana,geneva,sans-serif\"><span style=\"font-size:12px\">Il permet</span><span style=\"font-size:12px\">&nbsp;&agrave; ses utilisateurs un fonctionnement sans faille tout au long de la journ&eacute;e gr&acirc;ce &agrave;</span><span style=\"font-size:12px\">&nbsp;sa&nbsp;<span style=\"color:#000080\"><strong>batterie longue dur&eacute;e</strong></span>.&nbsp;</span></span></p>\r\n\r\n<p><span style=\"font-family:verdana,geneva,sans-serif\"><span style=\"font-size:12px\">Il dispose de la toute derni&egrave;re g&eacute;n&eacute;ration d&#39;<strong><span style=\"color:#000080\">imagers 2D</span></strong>&nbsp;qui assure une lecture performante, rapide et pr&eacute;cise &agrave; une tr&egrave;s longue distance.</span></span></p>\r\n\r\n<p><span style=\"font-family:verdana,geneva,sans-serif\"><span style=\"font-size:12px\">Il satisfait parfaitement aux applications de la grande distribution, de l&rsquo;industrie,&nbsp;du transport et de la logistique.</span></span></p>\r\n\r\n<p><span style=\"font-family:verdana,geneva,sans-serif\"><span style=\"font-size:12px\"><span style=\"color:rgb(70, 70, 70)\">Ce mat&eacute;riel </span><span style=\"color:rgb(0, 0, 128)\"><strong>disponible</strong></span><span style=\"color:rgb(70, 70, 70)\">&nbsp;chez Eurocomputer remplace au catalogue les </span><a href=\"http://www.eurocomputer.fr/terminaux-code-barres/intermec-honeywell-lxe/terminal-mobil