INSERT INTO `ec_products_multilingual` VALUES ('915', '467', '1', 'Opticon OPH-1005', '<p>Le terminal Opticon OPH-1005 est un <span style=\"color:#000080\"><strong>terminal pour inventaire</strong></span> dot&eacute; d&#39;une m&eacute;moire de 4Mo pour le stockage des donn&eacute;es.</p>\r\n\r\n<p>Il dispose&nbsp;d&#39;une t&ecirc;te de lecture laser performante permettant la capture de codes barres&nbsp;<span style=\"color:#000080\"><strong>1D et 2D&nbsp;</strong></span>de type PDF417<strong>.</strong><br />\r\n<br />\r\nSon socle de communication et chargement, propos&eacute; en option,&nbsp;vous permet de recharger sa batterie&nbsp;et de transmettre les donn&eacute;es &agrave; votre PC en&nbsp;connectique USB,&nbsp;s&eacute;rie ou encore par <span style=\"color:#000080\"><strong>infra-rouge</strong></span><strong>.</strong></p>\r\n\r\n<p><span style=\"color:#000080\"><strong>Robuste</strong></span>, il r&eacute;siste &agrave; l&#39;humidit&eacute;, &agrave; la poussi&egrave;re et aux chutes multiples d&#39;1,5 m&egrave;tres sur b&eacute;ton<strong>.</strong></p>\r\n\r\n<p>Ce mat&eacute;riel est disponible chez Eurocomputer.</p>\r\n', '0', '0', 'Opticon-OPH1005.pdf', '<p><span style=\"font-family:arial,sans-serif; font-size:10pt\">scanner&nbsp;</span>OPH-1005</p>\r\n\r\n<p><span style=\"font-family:arial,sans-serif; font-size:10pt\">lecteur&nbsp;</span>OPH-1005</p>\r\n\r\n<p><span style=\"font-family:arial,sans-serif; font-size:10pt\">terminal&nbsp;</span>OPH-1005</p>\r\n\r\n<p><span style=\"font-family:arial,sans-serif; font-size:10pt\">douchette&nbsp;</span>OPH-1005</p>\r\n\r\n<p><span style=\"font-family:arial,sans-serif; font-size:10pt\">scanner code-barres&nbsp;</span>OPH-1005</p>\r\n\r\n<p><span style=\"font-family:arial,sans-serif; font-size:10pt\">lecteur code-barres&nbsp;</span>OPH-1005</p>\r\n\r\n<p><span style=\"font-family:arial,sans-serif; font-size:10pt\">&nbsp;terminal code-barres&nbsp;</span>OPH-1005</p>\r\n\r\n<p><span style=\"font-family:arial,s