INSERT INTO `ec_products_multilingual` VALUES ('973', '496', '1', 'T730', '<p><span style=\"font-size:14px\"><span style=\"font-family:arial,helvetica,sans-serif\"><span style=\"color:rgb(87, 87, 87)\">Le</span><span style=\"color:#000080\"><strong> client l&eacute;ger HP T730</strong></span><span style=\"color:rgb(87, 87, 87)\">, est &agrave; ce jour le</span><span style=\"color:#000080\"><strong> client l&eacute;ger HP</strong></span><span style=\"color:rgb(87, 87, 87)\"> pour ordinateur de bureau le plus puissant du march&eacute;.&nbsp;</span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:arial,helvetica,sans-serif\"><span style=\"color:rgb(87, 87, 87)\">La</span><span style=\"color:#000080\"><strong> platine HP T730</strong></span><span style=\"color:rgb(87, 87, 87)\"> est adapt&eacute;e pour des d&eacute;ploiements sans d&eacute;lai dans des environnements exigeants, avec un processeur AMD </span><span style=\"color:#000080\"><strong>quadric&oelig;ur.</strong></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:arial,helvetica,sans-serif\"><span style=\"color:rgb(87, 87, 87)\">&nbsp;Le </span><span style=\"color:#000080\"><strong>HP T730</strong></span><span style=\"color:rgb(87, 87, 87)\"> a &eacute;t&eacute; con&ccedil;u avec une quantit&eacute; importante&nbsp;d&#39;interfaces un choix au niveau du syst&egrave;me d&#39;exploitation et du fournisseur de solutions ind&eacute;pendant, ainsi que la </span><strong><span style=\"color:#000080\">possibilit&eacute; de travailler sur&nbsp;4 &eacute;crans c&#39;est la technologie UHD/4K2</span></strong><span style=\"color:rgb(87, 87, 87)\">.</span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:arial,helvetica,sans-serif\"><span style=\"color:rgb(87, 87, 87)\">Ce mat&eacute;riel est&nbsp;</span><span style=\"color:#000080\"><strong>disponible </strong></span><span style=\"color:rgb(87, 87, 87)\">chez Eu